SELECT bpa.project_code AS projectCode, bpa.address AS address, bpa.lng, bpa.lat, bpm.project_name AS projectName, ifnull( if(p.contractor_type=16,sum(bpw.id IS NOT NULL ),sum(bpw.organization_code in (SELECT social_credit_number FROM sys_dept WHERE id = #{toggleDeptId} OR pids LIKE CONCAT( '%', #{toggleDeptId}, '%' )))) ,0) AS workerNum, if(p.contractor_type=16,0,1) AS cooType FROM ( SELECT id,project_code, address, lng, lat FROM buss_project_address WHERE id IN ( SELECT min( id ) FROM buss_project_address GROUP BY project_code )) bpa JOIN ( SELECT bp.project_code,bp.contractor_type FROM buss_project_sub_contractor bp WHERE id IN
#{item}
) p ON bpa.project_code = p.project_code JOIN buss_project_master bpm ON bpm.project_code = bpa.project_code AND bpm.is_del = 0 LEFT JOIN buss_project_worker bpw ON bpw.project_code = bpa.project_code AND bpw.is_del = 0 WHERE bpm.project_status = (select num from sys_dict where name = '在建' and pid = (select id from sys_dict where pid= 0 and name = '项目状态')) GROUP BY bpa.id
